import React, { Component } from 'react'

var color = true;

export class Disconnect extends Component {

    getStyle = () => {

        console.log(color);

        if (color) {
            color = false;
            return {
                backgroundColor: '#f4f4f4',
            }
        }
        else {
            color = true;
            return {
                backgroundColor: 'red',
            }
        }
    }

    changeColor = (e) => {
        console.log(' cambio color')
        return true
    }

    render() {
        return (
            <div style={this.getStyle()}>
                <h1> Disconnect Component</h1>
                <button type="button" onClick={this.props.disconnect} >Disconnect</button>
                <button type="button" onClick={this.changeColor} >color</button>
            </div>
        )
    }
}


export default Disconnect
