import React, { Component } from 'react';
import './App.css';
//import Graph from './components/graph';
import io from 'socket.io-client';
import './css/navbar.css'

import Disconnect from './components/disconnect';
import Connect from './components/connect';
import Stock from './components/stock';


const protocolo = "wss://";
const servidor = "le-18262636.bitzonte.com";
const ruta = "/stocks";

const socket = io(protocolo + servidor , {
  path: ruta
  });

class App extends Component {

  state = {
      stock: [
        {
          ticker: 'latam',
          volume: 0,
          time: 100,
        },
      ]
  }

  add_sell = (current) => {
    console.log('-- add sell --')
    console.log(current)
    var new_sell = true;
    this.setState({stocks: this.state.stock.map( data => {
      console.log(data.ticker, '==', current.ticker)

      if (data.ticker === current.ticker) {
        data.volume += current.volume
        new_sell = false
      }
    })

  })

  if (new_sell) {

    this.setState({stocks: this.state.stock.concat({
      ticker: current.ticker,
      volume: current.volume,
      time: current.time
    })      
    })
    }
  }

  disconnect = (e) => {
    socket.disconnect();
        console.log('###############################################');
        console.log('################## DISCONNECT #################');
        console.log('###############################################');
  }

  connect = (e) => {
    socket.connect();
    console.log('###############################################');
    console.log('##################  CONNECT   #################');
    console.log('###############################################');
  }



  componentDidMount() {
    console.log('Página montada')
    console.log('Encendiendo socket')

    /*socket.on("UPDATE", (data) => {

      console.log(data);
    console.log('\n-  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  ');
    });

    socket.on("BUY", (data) => {

      console.log(data);
    console.log('\n+ + + + + + + + + + + + + + + + + + + + + + + + + + + + + +  ');
    });*/

    socket.on("SELL", (data) => {
      this.add_sell(data)
    });

      //data.map((data) => (
          //
        //));


  }



  render() {

    //console.log(this.state.data)
    return (
      <div className="App">

        <ul className="nav-ul">
          <li className="nav-li"> <Disconnect disconnect={this.disconnect} />  </li>
          <li className="nav-li"><Connect connect={this.connect} /></li>
        </ul>
         <h1> Mercado de acciones  </h1>

         <Stock stock={this.state.stock}/>







      </div>
      );
    }
}





export default App;
