class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      updates: []
    };
    //this.startSocket = this.startSocket.bind(this);

  }



  componentDidMount() {
    console.log('P치gina montada')
    console.log('Encendiendo socket')

