import React, { Component } from 'react';
import './App.css';
import Graph from './components/graph';
import io from 'socket.io-client';

import Disconnect from './components/disconnect';
import Connect from './components/connect';

const protocolo = "wss://";
const servidor = "le-18262636.bitzonte.com";
const ruta = "/stocks";

const socket = io(protocolo + servidor , {
  path: ruta
  });

class App extends Component {

  state = {
      data: [
        {
          name: 'latam',
          price: 100,
          date: 1
        },
        {
          name: 'latam',
          price: 120,
          date: 2
        },
        {
          name: 'latam',
          price: 150,
          date: 3
        }
      ]
  }

  changeColor = () => {
    console.log(' cambio color');
    return true
}

  disconnect = (e) => {
    socket.disconnect();
        console.log('################################@@@@###########');
        console.log('################## DISCONNECT #################');
        console.log('################################@@@@###########');
  }

  connect = (e) => {
    socket.connect();
        console.log('################################@@@@###########');
        console.log('##################  CONNECT   #################');
        console.log('################################@@@@###########');
  }



  componentDidMount() {
    console.log('Página montada')
    console.log('Encendiendo socket')

    socket.on("UPDATE", (data) => {
      console.log(data);
    console.log('\n-  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  -  ');
    });
    }

  render() {

    console.log(this.state.data)
    return (
      <div className="App">

        <h1> Hello world </h1>

        <Disconnect disconnect={this.disconnect} />
        <Connect connect={this.connect} />
        <Graph />

      </div>
      );
    }
}





export default App;
