import React, { Component } from 'react';
import './App.css';
//import Graph from './components/graph';
import io from 'socket.io-client';
import './css/navbar.css'

import Disconnect from './components/disconnect';
import Connect from './components/connect';
import Stock from './components/stock';
import Price from './components/prices';


const protocolo = "wss://";
const servidor = "le-18262636.bitzonte.com";
const ruta = "/stocks";

const socket = io(protocolo + servidor , {
  path: ruta
  });

class App extends Component {

  state = {
      stock: [
      ],

      prices: [

      ]
  }
  update = (current) => {    
    console.log(this.state.prices)
    var new_ticker = true;

    this.setState({prices: this.state.prices.map( data => {  
        if (data.ticker === current.ticker) {
          data.value.push(current.value)
          data.time.push(current.time)
          var i = 0;
          var value = 0;

            for (i=0; i < data.value.lenght; i++) {
                value += data.value[i]
            }
            value = value/i
          data.avg = value

          new_ticker = false
        }
        return data
       }
        )

    })

  if (new_ticker) {
    var new_price = this.state.prices

    new_price.push({
      ticker: current.ticker,
      value: [current.value],
      avg: current.value,
      time: [current.time]
     })

    this.setState({prices: new_price})
    }

  }


  add_sell = (current) => {
    var new_sell = true;

    this.setState({stocks: this.state.stock.map( data => {
      console.log(data.ticker, '==', current.ticker)
      if (data.ticker === current.ticker) {
        data.volume += current.volume
        new_sell = false
      }
      return data
    })

  })

  if (new_sell) {

    var new_stock = this.state.stock
     new_stock.push({
      ticker: current.ticker,
      volume: current.volume,
      time: current.time
     })

    this.setState({stocks: new_stock})
    }
  }

  disconnect = (e) => {
    socket.disconnect();
        console.log('###############################################');
        console.log('################## DISCONNECT #################');
        console.log('###############################################');
  }

  connect = (e) => {
    socket.connect();
    console.log('###############################################');
    console.log('##################  CONNECT   #################');
    console.log('###############################################');
  }



  componentDidMount() {
    console.log('Página montada')
    console.log('Encendiendo socket')

    socket.on("UPDATE", (data) => {
      console.log('data socket update:', data)
      this.update(data)
    });

    socket.on("BUY", (data) => {

      //console.log(data);
    //console.log('\n+ + + + + + + + + + + + + + + + + + + + + + + + + + + + + +  ');
    });

    socket.on("SELL", (data) => {
      this.add_sell(data)
    });

      //data.map((data) => (
          //
        //));


  }



  render() {

    //console.log(this.state.data)
    return (
      <div className="App">

        <ul className="nav-ul">
          <li className="nav-li"> <Disconnect disconnect={this.disconnect} />  </li>
          <li className="nav-li"><Connect connect={this.connect} /></li>
        </ul>
         <h1> Mercado de acciones  </h1>

        <Stock stock={this.state.stock}/>
        <Price prices={this.state.prices}/> 
      </div>
      );
    }
}





export default App;
